// @author         DanielOnDiordna
// @name           Quick Draw Links
// @category       Layer
// @version        0.0.9.20210724.002500
// @updateURL      https://raw.githubusercontent.com/IITC-CE/Community-plugins/master/dist/DanielOnDiordna/quick-draw-links.meta.js
